import React, { useState, useEffect } from 'react';
//import CSRFToken from './csrftoken';
//import Cookies from 'js-cookie';

//const csrftoken = Cookies.get('csrftoken');

export default function Project(props){

    const [projectName, setProjectName] = useState();
    const [projectDescription, setProjectDescription] = useState();
    const [projectColumns, setProjectColumns] = useState([]);
    const [addColumn, setAddColumn] = useState(true);
    const [columnNum, setColumnNum] = useState(3);
    const [columnForm, setColumnForm] = useState(false);

    useEffect(() => {
        fetchProject();
    }, [])

    const fetchProject = async () => {
        const id = props.id;
        const response = await fetch(`/project/${id}`);
        const data = await response.json();
        console.log(data);
        setProjectName(data.project.name);
        setProjectDescription(data.project.description);
        setProjectColumns(data.columns);
        if (data.columns.length <= 3) {
            setColumnNum(3);
            setAddColumn(true);
        } else if (data.columns.length === 4 || data.columns.length === 5 ) {
            setColumnNum(2);
            setAddColumn(true);
        } else {
            setColumnNum(2);
            setAddColumn(false);
        }
    }

    return (
        <div>
            <h1>{projectName}</h1>
            <p className="fs-4 m-3">{projectDescription}</p>
            <div className="row">
                {projectColumns.map(column => (
                    <div key={column.id} id={column.id} className={`col-md-${columnNum}`}>
                        <div className="card-body shadow p-4 bg-white rounded">
                            <h5 className="card-title">{column.name}</h5>
                            <br/>
                            {column.tasks.map(task => (
                                <div key={task.id} id={task.id} className={`card border-${task.color} card-body mb-3`}>
                                    <h6 className="card-title">{task.name}</h6>
                                    <p className="card-text fw-light">{task.description}</p>
                                    <p className="fw-light fst-italic">{task.creation_date}</p>
                                </div>
                            ))}
                        </div>
                    </div>
                ))}
                {addColumn &&
                    <div className="col-md-3">
                        {projectForm ?
                            <div className="card-body shadow p-4 bg-light rounded">
                                <form onSubmit={handleCreateProject}>
                                    <CSRFToken />
                                    <input type="text" className="form-control" placeholder="Name" required onChange={e => setName(e.target.value)}/>
                                    <br/>
                                    <textarea rows="5" className="form-control" placeholder="Description" required onChange={e => setDescription(e.target.value)}></textarea>
                                    <br/>
                                    <input type="text" className="form-control" placeholder="Category" required onChange={e => setCategory(e.target.value)}/>
                                    <br/>
                                    <div className="d-grid gap-2">
                                        <input type="submit" className="btn btn-outline-primary" value="Create Project"/>
                                    </div>
                                </form>
                            </div>
                            :
                            <div className="d-grid gap-2">
                                <button className="btn" onClick={() => setProjectForm(true)}>
                                    <div className="card-body shadow-sm p-4 bg-light rounded d-flex">
                                        <p className="fs-1 m-3">+</p>
                                        <p className="fs-5 m-3">Click here to add a new project!</p>
                                    </div>
                                </button>
                            </div>
                        }
                    </div>
                }
            </div>
        </div>
    )
}
