import React, { useState } from 'react';
import { Link, Redirect } from "react-router-dom";
import Projects from "./Projects";

export default function Index(){

    const [username, setUsername] = useState(sessionStorage.getItem("loggedUser"));
    const [projects, setProjects] = useState([]);
    const [showProjects, setShowProjects] = useState(false);

    async function logout() {
        await fetch('/logout');
        sessionStorage.removeItem("loggedUser");
        setUsername('');
        return <Redirect to="/login" />
    }

    async function get_projects() {
        const response = await fetch('/projects');
        const data = await response.json();
        setProjects(data);
    }

    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <div className="container-fluid">
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarTogglerDemo03" aria-controls="navbarTogglerDemo03" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarTogglerDemo03">
                        <span class="navbar-brand mb-0 h1">{username}</span>
                        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                            <li className="nav-item">
                                <Link className="nav-link active" to="/">Home</Link>
                            </li>
                            <li className="nav-item">
                                <Link className="nav-link active" to="/projects" onClick={() => get_projects()}>Projects</Link>
                            </li>
                            <li className="nav-item">
                                <Link className="nav-link active" to="/login" onClick={() => logout()}>Logout</Link>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
            <div className="container">
                <br/>
                { showProjects ?
                <Projects /> :
                <h1>Home</h1>}
            </div>
        </div>
    )
}
