import React, { useState } from 'react';
import CSRFToken from './csrftoken';
import Cookies from 'js-cookie';

const csrftoken = Cookies.get('csrftoken');

export default function Projects(props){

    const [projectForm, setProjectForm] = useState(false);
    const [name, setName] = useState();
    const [description, setDescription] = useState();
    const [category, setCategory] = useState();
    const [message, setMessage] = useState();

    async function create_project() {
        const project_created = await fetch('/create_project', {
            method: 'POST',
            headers: {
              'X-CSRFToken': csrftoken
            },
            body: JSON.stringify({
              name: name,
              description: description,
              category: category
            })
          })
          .then(response => response.json());
        setMessage(project_created);
        console.log(message);
        setProjectForm(false);
    }

    return (
        <div>
            <h1>Projects</h1>
            <br/><br/>
            <div className="row">
                {props.projects.map(project => (
                    <div key={project.id} id={project.id} className="col-md-3">
                        <div className="card-body shadow p-4 bg-white rounded">
                            <h5 className="card-title">{project.name}</h5>
                            <p className="card-text">{project.description}</p>
                            <p className="card-text fw-light">Category: {project.category}</p>
                            <p className="fw-light fst-italic">{project.creation_date}</p>
                        </div>
                    </div>
                ))}
                <div className="col-md-3">
                    {projectForm ?
                        <div className="card-body shadow-sm p-4 bg-light rounded d-flex">
                            <form onSubmit={() => create_project()}>
                                <CSRFToken />
                            </form>
                        </div>
                        :
                        <div className="card-body shadow-sm p-4 bg-light rounded d-flex" onClick={() => setProjectForm(true)}>
                            <p className="fs-1 m-3">+</p>
                            <p className="fs-5 m-3">Click here to add a new project!</p>
                        </div>
                    }
                </div>
            </div>
        </div>
    )
}
