import React, { useState } from 'react';
import { Link, Redirect } from "react-router-dom";

export default function Index(){

    const [username, setUsername] = useState(sessionStorage.getItem("loggedUser"));
    const [projects, setProjects] = useState([]);

    async function logout() {
        await fetch('/logout');
        sessionStorage.removeItem("loggedUser");
        setUsername('');
        return <Redirect to="/login" />
    }

    async function get_projects() {
        const response = await fetch('/projects');
        const data = await response.json();
        setProjects(data);
    }

    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <div className="container-fluid">
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarTogglerDemo03" aria-controls="navbarTogglerDemo03" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarTogglerDemo03">
                        <li className="navbar-brand">{username}</li>
                        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                            <li className="nav-item">
                                <Link className="nav-link active" to="/">Home</Link>
                            </li>
                            <li className="nav-item">
                                <Link className="nav-link active" to="/login" onClick={() => logout()}>Logout</Link>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
            <div className="container">
                <br/>
                <h1>Projects</h1>
                <button onClick={() => get_projects()}>Projects</button>
                <br/><br/>
                <div className="row">
                    {projects.map(project => (
                        <div key={project.id} id={project.id} className="col-md-4">
                            <div className="card-body shadow p-4 bg-white rounded">
                                <h5 className="card-title">{project.name}</h5>
                                <p className="card-text">{project.description}</p>
                                <p className="card-text fw-light">Category: {project.category}</p>
                                <p className="fw-light fst-italic">{project.creation_date}</p>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    )
}
